import { useReducer } from "react";
import "./styles.css";

/*
INSTRUCTIONS / CONSIDERATIONS:

1. Let's implement a simple bank account! It's similar to the example that I used as an analogy to explain how useReducer works, but it's simplified (we're not using account numbers here)

2. Use a reducer to model the following state transitions: openAccount, deposit, withdraw, requestLoan, payLoan, closeAccount. Use the `initialState` below to get started.

3. All operations (expect for opening account) can only be performed if isActive is true. If it's not, just return the original state object. You can check this right at the beginning of the reducer

4. When the account is opened, isActive is set to true. There is also a minimum deposit amount of 500 to open an account (which means that the balance will start at 500)

5. Customer can only request a loan if there is no loan yet. If that condition is met, the requested amount will be registered in the 'loan' state, and it will be added to the balance. If the condition is not met, just return the current state

6. When the customer pays the loan, the opposite happens: the money is taken from the balance, and the 'loan' will get back to 0. This can lead to negative balances, but that's no problem, because the customer can't close their account now (see next point)

7. Customer can only close an account if there is no loan, AND if the balance is zero. If this condition is not met, just return the state. If the condition is met, the account is deactivated and all money is withdrawn. The account basically gets back to the initial state
*/

const initialState = {
  balance: 0,
  loan: 0,
  isActive: false,
  depositValue: "",
  withdrawValue: "",
  loanValue: "",
  paidLoanValue: "",
};

function reducer(state, action) {
  switch (action.type) {
    case "disable":
      return {
        ...state,
        isActive: state.balance === 0 ? action.payload : true,
        balance: action.payload ? 500 : state.balance,
      };
    case "setDepositValue":
      return {
        ...state,
        depositValue: action.payload,
      };
    case "deposit":
      return {
        ...state,
        balance: state.balance + action.payload,
        depositValue: "",
      };
    case "setWithdrawValue":
      return {
        ...state,
        withdrawValue: action.payload,
      };
    case "withdraw":
      return {
        ...state,
        balance: state.balance - action.payload,
        withdrawValue: "",
      };
    case "setLoanValue":
      return {
        ...state,
        loanValue: action.payload,
      };
    case "loan":
      return {
        ...state,
        balance: state.balance + action.payload,
        loan: action.payload,
        loanValue: "",
      };
    case "setPaidLoanValue":
      return {
        ...state,
        paidLoanValue: action.payload,
      };
    case "paidLoan":
      if (state.loan === "0") return;
      return {
        ...state,
        loan: state.loan > 0 ? state.loan - action.payload : state.loan,
        paidLoanValue: "",
      };
    default:
      throw new Error("Unknown Action");
  }
}

export default function App() {
  const [
    {
      balance,
      loan,
      isActive,
      depositValue,
      withdrawValue,
      loanValue,
      paidLoanValue,
    },
    dispatch,
  ] = useReducer(reducer, initialState);

  return (
    <div className="App">
      <h1>useReducer Bank Account</h1>
      <p>Balance: {balance}</p>
      <p>Loan: {loan}</p>

      <p>
        <button
          onClick={(e) => {
            dispatch({ type: "disable", payload: true });
          }}
          disabled={isActive}
        >
          Open account
        </button>
      </p>
      <p>
        <button
          onClick={() => dispatch({ type: "deposit", payload: depositValue })}
        >
          deposit
        </button>
        <input
          value={depositValue}
          onChange={(e) => {
            dispatch({
              type: "setDepositValue",
              payload: Number(e.target.value),
            });
          }}
          disabled={isActive ? false : true}
        />
      </p>

      <p>
        <button
          onClick={() => dispatch({ type: "withdraw", payload: withdrawValue })}
        >
          withdraw
        </button>
        <input
          value={withdrawValue}
          onChange={(e) => {
            dispatch({
              type: "setWithdrawValue",
              payload: Number(e.target.value),
            });
          }}
          disabled={isActive ? false : true}
        />
      </p>
      <p>
        <button
          onClick={() => dispatch({ type: "paidLoan", payload: paidLoanValue })}
        >
          pay loan
        </button>
        <input
          value={paidLoanValue}
          onChange={(e) => {
            dispatch({
              type: "setPaidLoanValue",
              payload: Number(e.target.value),
            });
          }}
          disabled={isActive ? false : true}
        />
      </p>
      <p>
        <button onClick={() => dispatch({ type: "loan", payload: loanValue })}>
          loan
        </button>
        <input
          value={loanValue}
          onChange={(e) => {
            dispatch({
              type: "setLoanValue",
              payload: Number(e.target.value),
            });
          }}
          disabled={isActive ? false : true}
        />
      </p>

      <p>
        <button
          onClick={() => {
            dispatch({ type: "disable", payload: false });
          }}
          disabled={isActive ? false : true}
        >
          Close account
        </button>
      </p>
    </div>
  );
}
